pragma solidity ^0.4.2;

contract Election {

  struct Candidate {
    uint id;
    string name;
    uint voteCount;
  }

  // key value pair
  mapping(uint => Candidate) public candidates;
  mapping(address => bool) public voters;

  uint public candidatesCount;

  function Election() public {
    addCandidate("Chris");
    addCandidate("Alice");
    addCandidate("Bob");
    addCandidate("Dave");
  }

  // logging function
  event votedEvent(uint indexed _vote);

  //private only this contract can call this following function
  function addCandidate(string _name) private {
    candidatesCount ++;
    candidates[candidatesCount] = Candidate(candidatesCount, _name, 0);
  }

  //public function, anyone can call
  function vote(uint _vote) public {

    // how to access the function callers address
    address _voter = msg.sender;

    // require that the voter has not voted before						
    // assert(voters[_voter] == false);
    require(!voters[_voter]);

    // require that the candidate is ok
    require(_vote > 0 && _vote <= candidatesCount);

    // add voter to voter dictionary
    voters[_voter] = true;

    // add the vote to the candidate
    candidates[_vote].voteCount ++;

    //trigger logging event
    votedEvent(_vote);
  }

}
